import React, { useContext, useState } from "react";
import { Button, TextField, Grid, Typography, Container, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { CopyToClipboard } from "react-copy-to-clipboard";
import { Assignment, Phone, PhoneDisabled } from "@material-ui/icons";

import { SocketContext } from "../SocketContext";

//stylesheet
const useStyles = makeStyles((theme) => ({
    root: {
        display: "flex",
        flexDirection: "column",
    },
    gridContainer: {
        minWidth: "200px",
        [theme.breakpoints.down("xs")]: {
            flexDirection: "column",
        },
    },
    container: {
        width: "50%",
        margin: "35px 0",
        padding: 0,
        [theme.breakpoints.down("xs")]: {
            width: "80%",
        },
    },
    margin: {
        marginTop: 20,
    },
    padding: {
        padding: 20,
    },
    paper: {
        padding: "5px 10px",
        border: "1px solid black",
    },
}))

//option component to display the name,id
const Options = ({ children }) => {
    const { me, callAccepted, name, setName, callEnded, callUser, leaveCall } = useContext(SocketContext);

    const [idToCall, setIdToCall] = useState("");

    const classes = useStyles();

    return (
        <Container className={classes.container}>
            <Paper elevation={10} className={classes.paper}>
                <form className={classes.root} noValidate autoComplete="off">
                    <Grid container className={classes.gridContainer}>
                        <Grid item xs={12} md={6} className={classes.padding}>
                            <Typography gutterBottom varient="h6">Account</Typography>
                            <TextField label="Name" value={name} onChange={(e) => setName(e.target.value)} fullWidth > </TextField>
                            <CopyToClipboard text={me} className={classes.margin}>
                                <Button variant="contained" color="primary" fullWidth startIcon={<Assignment fontSize="large" />}>Copy Your ID
                                </Button>
                            </CopyToClipboard>
                        </Grid>
                        <Grid item xs={12} md={6} className={classes.padding}>
                            <Typography gutterBottom varient="h6">Make a Call</Typography>
                            <TextField label="ID to call" value={idToCall} onChange={(e) => setIdToCall(e.target.value)} fullWidth></TextField>
                            {callAccepted && !callEnded ? (
                                <Button variant="contained"
                                    color="secondary"
                                    startIcon={<PhoneDisabled fontSize="large" />}
                                    fullWidth
                                    onClick={leaveCall}
                                    className={classes.margin}
                                >Hang Up</Button>
                            ) : (
                                <Button variant="contained"
                                    color="primary"
                                    startIcon={<Phone fontSize="large" />}
                                    fullWidth
                                    onClick={() => callUser(idToCall)}
                                    className={classes.margin}
                                >Call</Button>
                            )}
                        </Grid>
                    </Grid>
                </form>
                {children}
            </Paper>

        </Container>
    );
}

export default Options;
